+ (void)drawTerminalMarkerWithScalePercent: (CGFloat)scalePercent
{
    //// General Declarations
    CGContextRef context = UIGraphicsGetCurrentContext();


    //// Variable Declarations
    CGFloat scale = scalePercent * 0.01;

    //// disc Drawing
    CGContextSaveGState(context);
    CGContextTranslateCTM(context, 22.5, 21.5);
    CGContextScaleCTM(context, scale, scale);

    UIBezierPath* discPath = [UIBezierPath bezierPathWithOvalInRect: CGRectMake(-12.5, -12.5, 25, 25)];
    [FerryLifeStyleKit.bright setFill];
    [discPath fill];
    [FerryLifeStyleKit.dark setStroke];
    discPath.lineWidth = 4;
    [discPath stroke];

    CGContextRestoreGState(context);
}

