            UIBezierPath* larkspurFerryBldgRoutePath = UIBezierPath.bezierPath;
            [larkspurFerryBldgRoutePath moveToPoint: CGPointMake(-301.83, -1219.67)];
            [larkspurFerryBldgRoutePath addLineToPoint: CGPointMake(866.83, -50.03)];
            [larkspurFerryBldgRoutePath addLineToPoint: CGPointMake(866.83, 597.33)];
            larkspurFerryBldgRoutePath.miterLimit = 4;

            [FerryLifeStyleKit.disabled setStroke];
            larkspurFerryBldgRoutePath.lineWidth = 26;
