        //// Terminals
        {
            //// Pier 41 marker Drawing
            CGRect pier41MarkerRect = CGRectMake(404, 383, 287, 292);
            CGContextSaveGState(context);
            UIRectClip(pier41MarkerRect);
            CGContextTranslateCTM(context, pier41MarkerRect.origin.x, pier41MarkerRect.origin.y);
            CGContextScaleCTM(context, pier41MarkerRect.size.width / 44, pier41MarkerRect.size.height / 44);

            [FerryLifeStyleKit drawTerminalMarkerWithScalePercent: 100];
            CGContextRestoreGState(context);


            //// Pier 41 label Drawing
            CGRect pier41LabelRect = CGRectMake(35, 474, 405, 192);
            {
                NSString* textContent = @"Pier 41";
                NSMutableParagraphStyle* pier41LabelStyle = NSMutableParagraphStyle.defaultParagraphStyle.mutableCopy;
