                CGFloat pier41LabelTextHeight = [textContent boundingRectWithSize: CGSizeMake(pier41LabelRect.size.width, INFINITY)  options: NSStringDrawingUsesLineFragmentOrigin attributes: pier41LabelFontAttributes context: nil].size.height;
                CGContextSaveGState(context);
                CGContextClipToRect(context, pier41LabelRect);
                [textContent drawInRect: CGRectMake(CGRectGetMinX(pier41LabelRect), CGRectGetMinY(pier41LabelRect) + (CGRectGetHeight(pier41LabelRect) - pier41LabelTextHeight) / 2, CGRectGetWidth(pier41LabelRect), pier41LabelTextHeight) withAttributes: pier41LabelFontAttributes];
