#pragma mark Generated Images

+ (UIImage*)imageOfTerminalMarkerWithScalePercent: (CGFloat)scalePercent
{
    UIGraphicsBeginImageContextWithOptions(CGSizeMake(44, 44), NO, 0.0f);
    [FerryLifeStyleKit drawTerminalMarkerWithScalePercent: scalePercent];

    UIImage* imageOfTerminalMarker = UIGraphicsGetImageFromCurrentImageContext();
    UIGraphicsEndImageContext();

    return imageOfTerminalMarker;
}

+ (UIImage*)imageOfDisabledTerminalMarkerWithScalePercent: (CGFloat)scalePercent
{
    UIGraphicsBeginImageContextWithOptions(CGSizeMake(44, 44), NO, 0.0f);
    [FerryLifeStyleKit drawDisabledTerminalMarkerWithScalePercent: scalePercent];

    UIImage* imageOfDisabledTerminalMarker = UIGraphicsGetImageFromCurrentImageContext();
    UIGraphicsEndImageContext();

    return imageOfDisabledTerminalMarker;
}

+ (UIImage*)imageOfHiddenTerminalMarker
{
    if (_imageOfHiddenTerminalMarker)
        return _imageOfHiddenTerminalMarker;

    UIGraphicsBeginImageContextWithOptions(CGSizeMake(44, 44), NO, 0.0f);
    [FerryLifeStyleKit drawHiddenTerminalMarker];

    _imageOfHiddenTerminalMarker = UIGraphicsGetImageFromCurrentImageContext();
    UIGraphicsEndImageContext();

    return _imageOfHiddenTerminalMarker;
}

+ (UIImage*)imageOfSelectedTerminalMarkerWithScalePercent: (CGFloat)scalePercent
{
    UIGraphicsBeginImageContextWithOptions(CGSizeMake(44, 44), NO, 0.0f);
    [FerryLifeStyleKit drawSelectedTerminalMarkerWithScalePercent: scalePercent];

    UIImage* imageOfSelectedTerminalMarker = UIGraphicsGetImageFromCurrentImageContext();
    UIGraphicsEndImageContext();

    return imageOfSelectedTerminalMarker;
}

#pragma mark Customization Infrastructure

- (void)setHiddenTerminalMarkerTargets: (NSArray*)hiddenTerminalMarkerTargets
{
    _hiddenTerminalMarkerTargets = hiddenTerminalMarkerTargets;

    for (id target in self.hiddenTerminalMarkerTargets)
        [target setImage: FerryLifeStyleKit.imageOfHiddenTerminalMarker];
}


